package com.example.lab_2

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.navigation.fragment.findNavController
import androidx.recyclerview.widget.DividerItemDecoration
import androidx.recyclerview.widget.LinearLayoutManager
import com.example.lab_2.databinding.FragmentMainBinding
import com.example.lab_2.model.Food

class MainFragment : Fragment() {

    private var _binding: FragmentMainBinding? = null
    private val binding get() = _binding!!

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        _binding = FragmentMainBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        val layoutManager = LinearLayoutManager(requireContext())
        binding.recyclerView.layoutManager = layoutManager
        val dividerItemDecoration = DividerItemDecoration(
            binding.recyclerView.context,
            layoutManager.orientation
        )
        binding.recyclerView.addItemDecoration(dividerItemDecoration)

        val foodList = listOf(
            Food(
                name = "Spaghetti Bolognese",
                shortDescription = "Classic Italian pasta dish",
                description = "A delicious combination of pasta, meat sauce, and grated cheese.",
                url = "https://www.kitchensanctuary.com/wp-content/uploads/2019/09/Spaghetti-Bolognese-square-FS-0204.jpg"
            ),
            Food(
                name = "Chicken Tikka Masala",
                shortDescription = "Flavorful Indian curry",
                description = "Tender chicken cooked in a creamy tomato-based sauce with aromatic spices.",
                url = "https://www.seriouseats.com/thmb/DbQHUK2yNCALBnZE-H1M2AKLkok=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/chicken-tikka-masala-for-the-grill-recipe-hero-2_1-cb493f49e30140efbffec162d5f2d1d7.JPG"
            ),
            Food(
                name = "Sushi Rolls",
                shortDescription = "Japanese delicacy",
                description = "Fresh seafood, vegetables, and rice wrapped in seaweed.",
                url = "data:image/jpeg;base64,"
            ),
            Food(
                name = "Caesar Salad",
                shortDescription = "Classic salad with a tangy dressing",
                description = "Crisp romaine lettuce, croutons, Parmesan cheese, and Caesar dressing.",
                url = "https://food-images.files.bbci.co.uk/food/recipes/easy_caesar_salad_64317_16x9.jpg"
            ),
            Food(
                name = "Chocolate Brownies",
                shortDescription = "Decadent chocolate dessert",
                description = "Rich and fudgy chocolate brownies with a melt-in-your-mouth texture.",
                url = "https://www.theflavorbender.com/wp-content/uploads/2018/02/Fudgy-Brownies-The-Flavor-Bender-Featured-Image-SQ-16.jpg"
            ),
            Food(
                name = "Mango Sticky Rice",
                shortDescription = "Thai dessert delight",
                description = "Sweet sticky rice topped with ripe mango slices and drizzled with coconut sauce.",
                url = "https://hips.hearstapps.com/hmg-prod/images/delish-190618-mango-sticky-rice-255-landscape-pf-1561646936.png?crop=0.888888888888889xw:1xh;center,top&resize=1200:*"
            )
        )

        val adapter = FoodAdapter(foodList, findNavController())

        binding.recyclerView.adapter = adapter
    }


}
